import React from 'react';
import Header from './Header';
import PopupWithForm from './PopupWithForm';
import * as auth from '../utils/auth.js';

function Register() {
  return (
    <PopupWithForm
      popupName="popup_type_register"
      popupTitle="Регистрация"
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      buttonName="Зарегистрироваться"
      buttonNameIsLoad="Регистрация..."
    >
      <input
        id="email"
        type="email"
        name="email"
        autoComplete="email"
        className="popup__input popup__input_data_email"
        required
        minLength="2"
        maxLength="40"
        placeholder="Email"
        value={formValue.email}
        onChange={handleChangeEmail}
      />
      <span id="input-email-error" className="error"></span>
      <input
        id="password"
        name="password"
        type="password"
        autoComplete="new-password"
        className="popup__input popup__input_data_password"
        required
        minLength="2"
        maxLength="200"
        placeholder="Пароль"
        value={formValue.password}
        onChange={handleChangePassword}
      />
      <span id="input-password-error" className="error"></span>
    </PopupWithForm>
  );
}

export default Register;
