import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import Header from './Header';
import Authentication from './Authentication';
import * as auth from '../utils/auth.js';

function Login({ handleLogin }) {
  // Состояние формы входа в систему formValue (электронная почта, пароль).
  const [formValue, setFormValue] = useState({
    password: '',
    email: '',
  });
  // Состояние сообщения об ошибке:
  const [errorMessage, setErrorMessage] = useState('');
  // вызываем useNavigate(), для доступа к функции navigate (для перехода на другие страницы):
  const navigate = useNavigate(); 
  const location = useLocation();

  const checkToken = () => {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      auth
        .checkToken(jwt)
        .then((user) => {
          console.log('location', location);
          const url = location.state?.backUrl || '/main';
          handleLogin(user);
          navigate(url);
        })
        .catch((err) => console.log(err));
    }
  };

  useEffect(() => {
    checkToken();
  }, []);

  // Обработчик изменения полей формы:
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValue({
      ...formValue,
      [name]: value,
    });
  };

  // Обработчик отправки формы:
  const handleSubmit = (e) => {
    e.preventDefault();
    // проверяется, что введенные пароли совпадают. Если нет, то устанавливается сообщение
    // об ошибке с помощью setErrorMessage.
    if (!formValue.password || !formValue.email) {
      setErrorMessage('Both fields are required');
      return;
    }
    // извлекаются значения username и password из состояния formValue.
    const { password, email } = formValue;

    // если авторизацию апи проходим :
    auth
      .login( password, email)
      .then((data) => {
        if (data.jwt) {
          localStorage.setItem('jwt', data.jwt);
          handleLogin(data.user);
          const url = location.state?.backUrl || '/main';
          navigate(url);
        }
      })
      .catch((err) => {
        console.log(err);
        setErrorMessage(err);
      });
  };

  return (
    <Authentication authenticationName="login" authenticationTitle="Вход" onSubmit={handleSubmit} buttonName="Войти">
      <input
        id="email"
        type="email"
        name="email"
        autoComplete="email"
        className="authentication__input authentication__input_data_email"
        required
        minLength="2"
        maxLength="40"
        placeholder="Email"
        value={formValue.email}
        onChange={handleChange}
      />
      <span id="input-email-error" className="error"></span>
      <input
        id="password"
        name="password"
        type="password"
        autoComplete="new-password"
        className="authentication__input authentication__input_data_password"
        required
        minLength="2"
        maxLength="200"
        placeholder="Пароль"
        value={formValue.password}
        onChange={handleChange}
      />
      <span id="input-password-error" className="error"></span>
    </Authentication>
  );
}

export default Login;
