import React from 'react';
import avatarEditButton from '../images/Vector.svg';
import api from '../utils/api';
import Card from './Card';
// Импортируем объект контекста:
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick, onCardLike, cards }) {
  //states for user information and cards:
  // const [userName, setUserName] = React.useState('');
  // const [userDescription, setUserDescription] = React.useState('');
  // const [userAvatar, setUserAvatar] = React.useState([]);
  // const [cards, setCards] = React.useState([]);

  // Подписываемся на контекст CurrentUserContext:
  const currentUser = React.useContext(CurrentUserContext);

  //React.useEffect(() => {
    // api
    //   .getUserInfo()
    //   .then((data) => {
    //     setUserName(data.name);
    //     setUserDescription(data.about);
    //     setUserAvatar(data.avatar);
    //   })
    //   .catch((err) => console.log(err));
  //   api
  //     .getAllPlaces()
  //     .then((data) => {
  //       setCards(
  //         data.map((item) => ({
  //           likes: item.likes.length,
  //           id: item._id,
  //           src: item.link,
  //           alt: item.name,
  //           owner: item.owner,
  //           title: item.name,
  //         }))
  //       );
  //     })
  //     .catch((err) => console.log(err));
  // }, []);

  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__info">
            <div className="profile__avatar">
              <img
                className="profile__avatar-photo"
                src={currentUser.avatar}
                style={{ backgroundImage: `url(${currentUser.avatar})` }}
                alt="Фотография пользователя в профайле"
              />
              <img
                className="profile__avatar-edit"
                src={avatarEditButton}
                alt="Иконка редактирования аватарки"
                onClick={onEditAvatar}
              />
            </div>
            <div className="profile__description">
              <h1 className="profile__name">{currentUser.name}</h1>
              <button
                className="profile__edit-button"
                aria-label="Редактирование профиля"
                type="button"
                onClick={onEditProfile}
              ></button>
              <p className="profile__job">{currentUser.about}</p>
            </div>
          </div>
          <button
            className="profile__add-button"
            aria-label="Добавление контента в профиль"
            type="button"
            onClick={onAddPlace}
          ></button>
        </section>
        <div className="place-container">
          <ul className="elements">
            {
            cards.map((card) =>
              <Card 
              key={card._id} 
              card={card} 
              onCardClick={onCardClick} 
              onCardLike={onCardLike}
              // handleDeleteClick={onCardDelete}
              />           
            )}
          </ul>
        </div>
      </main>
    </>
  );
}

export default Main;
