import React from 'react';

function ImagePopup(card) {
    const isVisible=card?'popup_type_image ':"";
  return (
    <div className={`popup ${isVisible}`}>
      <div className="popup__container popup__container_type_image">
        <button className="popup__button-close" aria-label="Закрытие окна редактирования" type="button"></button>
        <img src={card ? card.link: ''} alt={card ? card.name: ''} className="popup__picture" />
        <h3 className="popup__picture-title">{card ? card.name: ''}</h3>
      </div>
    </div>
  );
}

export default ImagePopup;
