import React from 'react';
import avatarEditButton from '../images/Vector.svg';
import api from '../utils/api';
import Card from './Card';

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick }) {
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar, setUserAvatar] = React.useState([]);

  //state for cards:
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserInfo().then((data) => {
      setUserName(data.name);
    });
  }, []);

  React.useEffect(() => {
    api.getUserInfo().then((data) => {
      setUserDescription(data.about);
    });
  }, []);

  React.useEffect(() => {
    api.getAllPlaces().then((data) => {
      setCards(
        data.map((item) => ({
          likes: item.likes.length,
          id: item._id,
          src: item.link,
          alt: item.name,
          owner: item.owner,
          title: item.name,
        }))
      );
    });
  }, []);

  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__info">
            <div className="profile__avatar">
              <img className="profile__avatar-photo" src="#" alt="Фотография пользователя в профайле" />
              <img
                className="profile__avatar-edit"
                src={avatarEditButton}
                alt="Иконка редактирования аватарки"
                onClick={onEditAvatar}
              />
            </div>
            <div className="profile__description">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button"
                aria-label="Редактирование профиля"
                type="button"
                onClick={onEditProfile}
              ></button>
              <p className="profile__job">{userDescription}</p>
            </div>
          </div>
          <button
            className="profile__add-button"
            aria-label="Добавление контента в профиль"
            type="button"
            onClick={onAddPlace}
          ></button>
        </section>
        <div className="place-container">
          <ul className="elements">
            {cards.map(({ id, ...props }) => (
              <Card key={id} {...props} onCardClick={onCardClick} />
            ))}
          </ul>
        </div>
      </main>
    </>
  );
}

export default Main;
