import React from 'react';
import avatarEditButton from '../images/Vector.svg';
import api from '../utils/api';
import Card from './Card';

function Main({onEditProfile}) {
  //state for cards:
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getAllPlaces().then((data) => {
      setCards(
        data.map((item) => ({
        likes: item.likes.length,
        id: item._id,
        src: item.link,
        alt: item.name,
        owner: item.owner,
        title: item.name,
    }))
    )
    });
  },[]);

//   const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
//   function handleEditProfileClick() {
//     // const popupEditProfile = document.querySelector('.popup_type_edit');
//     // popupEditProfile.classList.add('popup_is-opened');
//     setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
//   }


  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__info">
            <div className="profile__avatar">
              <img className="profile__avatar-photo" src="#" alt="Фотография пользователя в профайле" />
              <img
                className="profile__avatar-edit"
                src={avatarEditButton}
                alt="Иконка редактирования аватарки"
                onClick={onEditAvatar}
              />
            </div>
            <div className="profile__description">
              <h1 className="profile__name">Жакки Кусто</h1>
              <button
                className="profile__edit-button"
                aria-label="Редактирование профиля"
                type="button"
                onClick={onEditProfile}
              ></button>
              <p className="profile__job">Ученый</p>
            </div>
          </div>
          <button
            className="profile__add-button"
            aria-label="Добавление контента в профиль"
            type="button"
            onClick={onAddPlace}
          ></button>
        </section>
        <div className="place-container">
            {
                cards.map(({id, ...props}) => <Card key={id} {...props} />)
            }
        </div>
      </main>
    </>
  );
}

export default Main;
