import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
//import Card from './Card';
import React from 'react';
import api from '../utils/api';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function App() {
  // Стейт, отвечающий за данные текущего пользователя
  const [currentUser, setCurrentUser] = React.useState({});
  // стейт для хранения карточек:
  const [cards, setCards] = React.useState([]);
  //switch state for edit profile popup:
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  //switch state for edit avatar popup:
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  //switch state for adding a place popup:
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  //switch state for image popup:
  const [selectedCard, setSelectedCard] = React.useState(null);

  //эффект обращения к API за инфо о пользователе и начальными карточками:
  React.useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => console.log(err));
    api
      .getAllPlaces()
      .then((data) => {
        setCards(
          data.map((item) => ({
            likes: item.likes,
            _id: item._id,
            src: item.link,
            alt: item.name,
            owner: item.owner,
            title: item.name,
          }))
        );
      })
      .catch((err) => console.log(err));
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleCardClick(curentCard) {
    setSelectedCard(curentCard);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(null);
    setIsAddPlacePopupOpen(null);
    setIsEditAvatarPopupOpen(null);
    setSelectedCard(null);
  }

  function handleCardDelete(id) {
    api.deletePlace(id).then(() => {
      /* используя методы массива, создаем новый массив карточек newCards, где не будет карточки, которую мы только что удалили */
      setCards(
        data.map((item) => ({
          likes: item.likes,
          _id: item._id,
          src: item.link,
          alt: item.name,
          owner: item.owner,
          title: item.name,
        }))
      );
    });
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.likeCard(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      {/* Внедряем» данные из currentUser с помощью провайдера контекста */}
      <div className="App">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
          onCardDelete={handleCardDelete}
          onCardLike={handleCardLike}
          cards={cards}
        />
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        <PopupWithForm
          popupName="popup_type_edit"
          popupTitle="Редактировать профиль"
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          buttonName="Сохранить"
          buttonNameIsLoad="Сохранение..."
        >
          <input
            id="input-name"
            type="text"
            name="name"
            className="popup__input popup__input_data_name"
            required
            minLength="2"
            maxLength="40"
            placeholder="Имя"
          />
          <span id="input-name-error" className="error"></span>
          <input
            id="input-job"
            type="text"
            name="job"
            className="popup__input popup__input_data_job"
            required
            minLength="2"
            maxLength="200"
            placeholder="О себе"
          />
          <span id="input-job-error" className="error"></span>
        </PopupWithForm>
        <PopupWithForm
          popupName="popup_type_card"
          popupTitle="Новое место"
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          buttonName="Создать"
          buttonNameIsLoad="Создание..."
        >
          <input
            id="input-place"
            minLength="2"
            maxLength="30"
            type="text"
            name="place"
            placeholder="Название"
            className="popup__input popup__input_data_place"
            required
          />
          <span id="input-place-error" className="error"></span>
          <input
            id="input-link-card"
            type="url"
            name="link"
            placeholder="Ссылка на картинку"
            className="popup__input popup__input_data_link"
            required
          />
          <span id="input-link-card-error" className="error"></span>
        </PopupWithForm>
        <PopupWithForm popupName="popup_type_deleteCard" popupTitle="Вы уверены?" buttonName="Да"></PopupWithForm>
        <PopupWithForm
          popupName="popup_type_avatar"
          popupTitle="Обновить аватар"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          buttonName="Сохранить"
          buttonNameIsLoad="Сохранение..."
        >
          <input
            id="input-link-avatar"
            type="url"
            name="link"
            placeholder="Ссылка на аватар"
            className="popup__input popup__input_data_link"
            required
          />
          <span id="input-link-avatar-error" className="error"></span>
        </PopupWithForm>
        <Footer />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
