import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import headerLogo from '../images/header-logo.svg';

function Header() {
  //const navigate = useNavigate();

  // function signOut() {
  //   localStorage.removeItem('jwt');
  //   navigate('/login');
  // }


  const location = useLocation();
  const isOnSignUpPage = location.pathname==="/sign-up";
  const isOnSignInPage = location.pathname==="/sign-in";

  let loginControl;
  if (isOnSignUpPage)
    loginControl = (
      <Link to="/sign-in" className="header__button-title">
        Войти
      </Link>
    );
  else if (isOnSignInPage)
    loginControl = (
      <Link to="/sign-up" className="header__button-title">
        Регистрация
      </Link>
    );
  return (
    <header className="header">
      <img src={headerLogo} alt="Логотип Место" className="logo" />
      <button
        type="submit"
        className="header__button-authentication header__signin"
        aria-label="Перейти"
        //onClick={signIn}
      >
        {loginControl}
      </button>
      {/* <button
        type="submit"
        className="header__button-authentication header__signup"
        aria-label="Перейти"
        //onClick={signUp}
      >

      </button> */}
    </header>
  );
}

export default Header;
